import React, { useState } from "react";
import Chart from "chart.js/auto";
import { CategoryScale } from "chart.js";
import { Line } from "react-chartjs-2";

Chart.register(CategoryScale);

export default function LineChart() {
  const [data, setData] = useState(null);
  const [timePeriod, setTimePeriod] = useState("lastDay"); 
  const [interval, setInterval] = useState(300); 
  const [ipAddress, setIpAddress] = useState("172.31.88.161"); 
  const [loading, setLoading] = useState(false); 

  // Handle data fetching based on timePeriod, interval, and IP address
  const fetchData = async () => {
    if (!ipAddress) return alert("IP Address is required!");

    setLoading(true); // Start loading

    try {
      const response = await fetch(
        `http://localhost:3000/cpu-usage?timePeriod=${timePeriod}&interval=${interval}&ipAddress=${ipAddress}`
      );
      const result = await response.json();
      console.log("Fetched data:", result); // Log the fetched data

      if (Array.isArray(result.metrics.Datapoints)) {
        // Sort the data by Timestamp in ascending order
        const sortedData = result.metrics.Datapoints.sort(
          (a, b) => new Date(a.Timestamp) - new Date(b.Timestamp)
        );
        setData(sortedData); // Set sorted data
      } else {
        console.error("Error: 'metrics' is not an array.");
        setData([]); // Set an empty array if the metrics are not an array
      }
    } catch (error) {
      console.error("Error fetching data:", error);
    } finally {
      setLoading(false); // Stop loading
    }
  };


  // Prepare the chart data
  const chartData = {
    labels: data?.map((point) =>
      new Date(point.Timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
    ),
        datasets: [
      {
        label: "CPU Usage (%)",
        data: data?.map((point) => point.Average), // Assuming Average is the metric you want
        borderColor: "rgb(75, 192, 192)",
        backgroundColor: "rgba(75, 192, 192, 0.2)",
        borderWidth: 2,
      },
    ],
  };

  return (
    <div className="chart-container">
      <h1>CPU Usage Over Time</h1>

      {/* Time period selection */}
      <div>
        <label>Time Period: </label>
        <select
          value={timePeriod}
          onChange={(e) => setTimePeriod(e.target.value)}
        >
          <option value="lastDay">Last Day</option>
          <option value="lastMonth">Last Month</option>
          <option value="lastYear">Last Year</option>
        </select>
      </div>

      {/* Period input (in seconds) */}
      <div>
        <label>Interval (seconds): </label>
        <input
          type="number"
          value={interval}
          onChange={(e) => setInterval(e.target.value)}
        />
      </div>

      {/* IP address input */}
      <div>
        <label>IP Address: </label>
        <input
          type="text"
          value={ipAddress}
          onChange={(e) => setIpAddress(e.target.value)}
        />
      </div>

      {/* Load button */}
      <div>
        <button onClick={fetchData} disabled={loading}>
          {loading ? "Loading..." : "Load"}
        </button>
      </div>

      <h2 style={{ textAlign: "center" }}>Line Chart</h2>
      {data ? (
        <Line
          data={chartData}
          options={{
            elements: {
              line: {
                borderJoinStyle: "round",
              },
            },
            plugins: {
              title: {
                display: true,
                text: "CPU Usage Over Time",
              },
              legend: {
                display: false,
              },
            },
          }}
        />
      ) : (
        !loading && <p>No data to display. Please load data.</p>
      )}
    </div>
  );
}
