import React, { useEffect, useState } from "react";
import Chart from "chart.js/auto";
import { CategoryScale } from "chart.js";
import { Line } from "react-chartjs-2";

Chart.register(CategoryScale);

export default function LineChart() {
  const [data, setData] = useState(null);
  const [timePeriod, setTimePeriod] = useState("lastDay"); // default to last day
  const [period, setPeriod] = useState(300); // Period in seconds
  const [ipAddress, setIpAddress] = useState(""); // IP address input

  // Handle data fetching based on timePeriod, period, and IP address
  useEffect(() => {
    const fetchData = async () => {
      if (!ipAddress) return; // Skip fetch if IP address is empty

      try {
        const response = await fetch(
          `http://localhost:3000/cpu-usage?timePeriod=${timePeriod}&period=${period}&ipAddress=${ipAddress}`
        );
        const result = await response.json();
        setData(result.metrics);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [timePeriod, period, ipAddress]); // Re-fetch data when any of the inputs change

  // Prepare the chart data
  const chartData = {
    labels: data?.map((point) => point.Timestamp), // Assuming metrics have Timestamp field
    datasets: [
      {
        label: "CPU Usage (%)",
        data: data?.map((point) => point.Average), // Assuming Average is the metric you want
        borderColor: "rgb(75, 192, 192)",
        backgroundColor: "rgba(75, 192, 192, 0.2)",
        borderWidth: 2,
      },
    ],
  };

  return (
    <div className="chart-container">
      <h1>CPU Usage Over Time</h1>

      {/* Time period selection */}
      <div>
        <label>Time Period: </label>
        <select
          value={timePeriod}
          onChange={(e) => setTimePeriod(e.target.value)}
        >
          <option value="lastDay">Last Day</option>
          <option value="lastMonth">Last Month</option>
          <option value="lastYear">Last Year</option>
        </select>
      </div>

      {/* Period input (in seconds) */}
      <div>
        <label>Period (seconds): </label>
        <input
          type="number"
          value={period}
          onChange={(e) => setPeriod(e.target.value)}
        />
      </div>

      {/* IP address input */}
      <div>
        <label>IP Address: </label>
        <input
          type="text"
          value={ipAddress}
          onChange={(e) => setIpAddress(e.target.value)}
        />
      </div>

      <h2 style={{ textAlign: "center" }}>Line Chart</h2>
      {data ? (
        <Line
          data={chartData}
          options={{
            elements: {
              line: {
                borderJoinStyle: "round",
              },
            },
            plugins: {
              title: {
                display: true,
                text: "CPU Usage Over Time",
              },
              legend: {
                display: false,
              },
            },
          }}
        />
      ) : (
        "Loading data..."
      )}
    </div>
  );
}
